import React, { useState, useEffect, useRef } from 'react';
import MicRecorder from 'mic-recorder-to-mp3';
import { FaUserCircle, FaRobot, FaMicrophone, FaFilePdf, FaUpload } from 'react-icons/fa';
import './App.css';

const recorder = new MicRecorder({ bitRate: 128 });

function App() {
  const [messages, setMessages] = useState([]);
  const [input, setInput] = useState('');
  const [transcription, setTranscription] = useState('');
  const [isRecording, setIsRecording] = useState(false);
  const [recordingSeconds, setRecordingSeconds] = useState(30);
  const [audioUrl, setAudioUrl] = useState('');
  const [isTyping, setIsTyping] = useState(false);
  const chatWindowRef = useRef(null);
  const intervalRef = useRef(null);

  useEffect(() => {
    if (chatWindowRef.current) {
      chatWindowRef.current.scrollTop = chatWindowRef.current.scrollHeight;
    }
  }, [messages]);

  const handleSend = async () => {
    if (!input.trim() || !transcription) return;
    const userMessage = { text: input, sender: 'user' };
    setMessages(prev => [...prev, userMessage]);
    setInput('');
    setIsTyping(true);

    const res = await fetch('http://localhost:8000/chat', {
      method: 'POST',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify({ prompt: input, context: transcription })
    });
    const data = await res.json();
    setIsTyping(false);
    setMessages(prev => [...prev, { text: data.response, sender: 'bot' }]);
  };

  const handleFileUpload = async (e) => {
    const file = e.target.files[0];
    if (!file) return;
    const formData = new FormData();
    formData.append('audio', file);

    const res = await fetch('http://localhost:8000/transcribe', {
      method: 'POST',
      body: formData
    });
    const data = await res.json();
    setTranscription(data.transcription || '[No voice detected]');
  };

  const handlePDFExport = async () => {
    if (!transcription) return alert("Please transcribe audio first.");
    const res = await fetch('http://localhost:8000/export/pdf', {
      method: 'POST',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify({ prompt: transcription })
    });
    const data = await res.json();
    if (data.file) window.open(`http://localhost:8000/${data.file}`, '_blank');
  };

  const stopRecording = async () => {
    const [buffer, blob] = await recorder.stop().getMp3();
    const file = new File([blob], "mic_recording.mp3", { type: "audio/mp3" });
    const url = URL.createObjectURL(blob);
    setAudioUrl(url);
    setIsRecording(false);
    clearInterval(intervalRef.current);

    const formData = new FormData();
    formData.append("audio", file);

    const res = await fetch("http://localhost:8000/transcribe", {
      method: "POST",
      body: formData
    });
    const data = await res.json();
    setTranscription(data.transcription || '[No voice detected]');
  };

  const handleMicClick = async () => {
    if (!isRecording) {
      try {
        await recorder.start();
        setIsRecording(true);
        intervalRef.current = setInterval(() => {
          setRecordingSeconds(prev => {
            if (prev <= 1) {
              stopRecording();
              return 0;
            }
            return prev - 1;
          });
        }, 1000);
      } catch {
        alert("Microphone access denied.");
      }
    } else {
      stopRecording();
    }
  };

  return (
    <div className="app grid grid-cols-12 min-h-screen bg-primary text-white">
      {/* Left Panel */}
      <div className="col-span-2 bg-panel p-4 rounded-box flex flex-col items-center gap-6 shadow-lg">
        <img src="logo.png" alt="Logo" className="w-24 h-24 rounded-full" />
        <label className="action-box">
          <FaUpload className="text-2xl mb-2" />
          Upload
          <input type="file" accept=".mp3,.wav" onChange={handleFileUpload} hidden />
        </label>
        <button className="action-box" onClick={handleMicClick}>
          <FaMicrophone className="text-2xl mb-2" />
          Mic
        </button>
        <button className="action-box" onClick={handlePDFExport}>
          <FaFilePdf className="text-2xl mb-2" />
          Export
        </button>
        <img src="call_center.jpg" alt="Call" className="rounded mt-auto h-40 w-full object-cover" />
      </div>

      {/* Center Chat Panel */}
      <div className="col-span-7 bg-panel p-4 rounded-box flex flex-col justify-between shadow-lg">
        <h1 className="text-xl text-center font-bold border-b border-white/10 pb-2 mb-2">AI Call Summarization Chatbot</h1>
        <div className="flex-1 overflow-y-auto px-2" ref={chatWindowRef}>
          {messages.map((msg, i) => (
            <div key={i} className={`flex items-start gap-2 mb-3 ${msg.sender === 'user' ? 'justify-end' : 'justify-start'}`}>
              <span className="text-2xl">{msg.sender === 'user' ? 'üßë' : 'ü§ñ'}</span>
              <div className={`px-3 py-2 rounded-lg max-w-lg ${msg.sender === 'user' ? 'bg-chatUser text-black' : 'bg-chatBot text-white'}`}>
                {msg.text}
              </div>
            </div>
          ))}
          {isTyping && (
            <div className="flex items-center gap-2 text-sm text-gray-400">
              <FaRobot /> <em>Typing...</em>
            </div>
          )}
        </div>
        <input
          type="text"
          value={input}
          onChange={e => setInput(e.target.value)}
          onKeyDown={e => e.key === 'Enter' && handleSend()}
          className="mt-2 p-3 rounded-lg bg-white text-black"
          placeholder="Ask a question..."
        />
      </div>

      {/* Right Panel */}
      <div className="col-span-3 bg-panel p-4 rounded-box shadow-lg flex flex-col gap-4">
        {isRecording && (
          <div className="waveform-box">
            üéôÔ∏è Recording... {recordingSeconds}s left
          </div>
        )}
        {audioUrl && <audio controls src={audioUrl} className="w-full" />}
        <div className="flex flex-col flex-1 overflow-hidden">
          <label className="text-sm font-semibold mb-1">Transcript Preview</label>
          <textarea
            value={transcription}
            readOnly
            className="flex-1 resize-y rounded-lg p-2 bg-gray-900 text-white border border-gray-700 overflow-y-auto"
            rows={8}
          />
        </div>
      </div>
    </div>
  );
}

export default App;
