import React, { useState } from "react";
import "./App.css";
import { uploadAudio, startMicRecording } from "./audioUtils";
import axios from "axios";

function App() {
  const [theme, setTheme] = useState("dark");
  const [transcript, setTranscript] = useState("");
  const [messages, setMessages] = useState([]);
  const [input, setInput] = useState("");

  const toggleTheme = () => {
    setTheme(theme === "dark" ? "light" : "dark");
  };

  const handleAudioUpload = async (e) => {
    const file = e.target.files[0];
    if (!file) return;
    const text = await uploadAudio(file);
    const time = new Date().toLocaleTimeString();
    setMessages([...messages, { sender: "system", text: `Audio Transcript:\n${text}`, time }]);
    setTranscript(text);
  };

  const handleMicRecord = async () => {
    const text = await startMicRecording();
    const time = new Date().toLocaleTimeString();
    setMessages([...messages, { sender: "system", text: `Mic Transcript:\n${text}`, time }]);
    setTranscript(text);
  };

  const handleChat = async () => {
    if (!input.trim()) return;
    const time = new Date().toLocaleTimeString();
    const newMessages = [...messages, { sender: "user", text: input, time }];
    setMessages(newMessages);
    setInput("");

    try {
      const res = await axios.post("http://127.0.0.1:8000/chat", {
        prompt: input,
        transcript: transcript,
        session_id: "session1",
      });
      newMessages.push({ sender: "bot", text: res.data.response, time: new Date().toLocaleTimeString() });
      setMessages([...newMessages]);
    } catch (err) {
      newMessages.push({ sender: "bot", text: "Error: Could not fetch response", time: new Date().toLocaleTimeString() });
      setMessages([...newMessages]);
    }
  };

  const handleExport = async (type) => {
    const endpoint = type === "pdf" ? "/export/pdf" : "/export/word";
    try {
      const res = await axios.post(`http://127.0.0.1:8000${endpoint}`, { transcript }, { responseType: "blob" });
      const blob = new Blob([res.data]);
      const link = document.createElement("a");
      link.href = window.URL.createObjectURL(blob);
      link.download = `summary.${type}`;
      link.click();
    } catch (err) {
      alert(`Failed to export ${type.toUpperCase()}`);
    }
  };

  return (
    <div className={`app ${theme}`}>
      <header>
        <h1>AI Call Summarizer</h1>
        <button onClick={toggleTheme}>Toggle {theme === "dark" ? "Light" : "Dark"}</button>
      </header>

      <div className="main-layout">
        {/* Left Panel */}
        <div className="left-panel">
          <label className="upload-label">
            Upload Audio
            <input type="file" accept="audio/*" onChange={handleAudioUpload} style={{ display: "none" }} />
          </label>
          <button onClick={handleMicRecord}>Mic Record</button>
          <div className="export-buttons">
            <button onClick={() => handleExport("pdf")}>Export PDF</button>
            <button onClick={() => handleExport("word")}>Export Word</button>
          </div>
        </div>

        {/* Chat Panel */}
        <div className="chat-panel">
          {messages.map((msg, i) => (
            <div key={i} className="chat-msg">
              <span className="timestamp">[{msg.time}]</span>
              <strong>{msg.sender === "user" ? "You: " : msg.sender === "bot" ? "AI: " : ""}</strong> {msg.text}
            </div>
          ))}
          <div className="chat-input">
            <input
              type="text"
              placeholder="Ask about the call..."
              value={input}
              onChange={(e) => setInput(e.target.value)}
              onKeyDown={(e) => e.key === "Enter" && handleChat()}
            />
            <button onClick={handleChat}>Send</button>
          </div>
        </div>

        {/* Right Panel */}
        <div className="right-panel">
          <h4>Transcript Preview</h4>
          <div className="transcript-box">{transcript}</div>
        </div>
      </div>
    </div>
  );
}

export default App;
