import React, { useState, useEffect, useRef } from 'react';
import './App.css';
import MicRecorder from 'mic-recorder-to-mp3';
import { FaMicrophone, FaFilePdf, FaUpload, FaUserCircle, FaRobot } from 'react-icons/fa';

const recorder = new MicRecorder({ bitRate: 128 });

function App() {
  const [messages, setMessages] = useState([]);
  const [input, setInput] = useState('');
  const [transcription, setTranscription] = useState('');
  const [isRecording, setIsRecording] = useState(false);
  const [audioUrl, setAudioUrl] = useState('');
  const chatRef = useRef(null);

  useEffect(() => {
    if (chatRef.current) {
      chatRef.current.scrollTop = chatRef.current.scrollHeight;
    }
  }, [messages]);

  const handleSend = async () => {
    if (!input || !transcription) return;
    setMessages((prev) => [...prev, { text: input, sender: 'user' }]);
    setInput('');
    const res = await fetch("http://localhost:8000/chat", {
      method: 'POST',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify({ prompt: input, context: transcription }),
    });
    const data = await res.json();
    setMessages((prev) => [...prev, { text: data.response, sender: 'bot' }]);
  };

  const handleUpload = async (e) => {
    const file = e.target.files[0];
    const form = new FormData();
    form.append("audio", file);
    const res = await fetch("http://localhost:8000/transcribe", {
      method: 'POST',
      body: form,
    });
    const data = await res.json();
    setTranscription(data.transcription || '');
  };

  const startRecording = async () => {
    await recorder.start();
    setIsRecording(true);
  };

  const stopRecording = async () => {
    const [buffer, blob] = await recorder.stop().getMp3();
    const form = new FormData();
    form.append("audio", new File([blob], "record.mp3", { type: 'audio/mp3' }));
    setIsRecording(false);
    const url = URL.createObjectURL(blob);
    setAudioUrl(url);
    const res = await fetch("http://localhost:8000/transcribe", {
      method: 'POST',
      body: form,
    });
    const data = await res.json();
    setTranscription(data.transcription || '');
  };

  const handleExport = async () => {
    const res = await fetch("http://localhost:8000/export/pdf", {
      method: 'POST',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify({ prompt: transcription }),
    });
    const data = await res.json();
    if (data.file) window.open(`http://localhost:8000${data.file}`, '_blank');
  };

  return (
    <div className="app-container">
      {/* Left Panel */}
      <div className="panel left-panel">
        <img src="/logo.png" alt="Logo" className="logo" />
        <button className="action-box">
          <label>
            <FaUpload /> Upload
            <input type="file" accept=".mp3,.wav" onChange={handleUpload} hidden />
          </label>
        </button>
        <button className="action-box" onClick={isRecording ? stopRecording : startRecording}>
          <FaMicrophone /> {isRecording ? "Stop" : "Record"}
        </button>
        <button className="action-box" onClick={handleExport}>
          <FaFilePdf /> Export
        </button>
        <img src="/call_center.jpg" alt="Call Center" className="call-img" />
      </div>

      {/* Center Chat Panel */}
      <div className="panel chat-panel">
        <h1 className="chat-title">AI Call Summarization Chatbot</h1>
        <div className="chat-window" ref={chatRef}>
          {messages.map((msg, idx) => (
            <div key={idx} className={`chat-message ${msg.sender}`}>
              {msg.sender === 'user' ? <FaUserCircle /> : <FaRobot />}
              <span>{msg.text}</span>
            </div>
          ))}
        </div>
        <div className="chat-input">
          <input
            type="text"
            value={input}
            onChange={(e) => setInput(e.target.value)}
            onKeyDown={(e) => e.key === 'Enter' && handleSend()}
            placeholder="Type your question..."
          />
        </div>
      </div>

      {/* Right Panel */}
      <div className="panel right-panel">
        <div className="waveform-box">
          {audioUrl ? <audio src={audioUrl} controls /> : <div>ðŸŽµ Waveform Preview</div>}
        </div>
        <div className="transcript-box">
          <strong>Transcript Preview</strong>
          <textarea className="transcript-scroll" value={transcription} readOnly />
        </div>
      </div>
    </div>
  );
}

export default App;
