// src/App.jsx
import React, { useState, useEffect, useRef } from 'react';
import MicRecorder from 'mic-recorder-to-mp3';
import { FaUserCircle, FaRobot, FaMicrophone, FaFilePdf, FaUpload } from 'react-icons/fa';
import './index.css';

const recorder = new MicRecorder({ bitRate: 128 });

function App() {
  const [messages, setMessages] = useState([]);
  const [input, setInput] = useState('');
  const [transcription, setTranscription] = useState('');
  const [isRecording, setIsRecording] = useState(false);
  const [recordingSeconds, setRecordingSeconds] = useState(30);
  const [audioUrl, setAudioUrl] = useState('');
  const intervalRef = useRef(null);
  const chatWindowRef = useRef(null);

  useEffect(() => {
    if (chatWindowRef.current) {
      chatWindowRef.current.scrollTop = chatWindowRef.current.scrollHeight;
    }
  }, [messages]);

  const handleSend = async () => {
    if (!input.trim() || !transcription) return;
    setMessages(prev => [...prev, { text: input, sender: 'user' }]);
    setInput('');
    const res = await fetch('http://localhost:8000/chat', {
      method: 'POST',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify({ prompt: input, context: transcription })
    });
    const data = await res.json();
    setMessages(prev => [...prev, { text: data.response, sender: 'bot' }]);
  };

  const handleFileUpload = async (e) => {
    const file = e.target.files[0];
    if (!file) return;
    const formData = new FormData();
    formData.append('audio', file);
    const res = await fetch('http://localhost:8000/transcribe', {
      method: 'POST',
      body: formData
    });
    const data = await res.json();
    if (!data.transcription || data.transcription.includes("No voice detected")) {
      alert("No voice detected in the audio.");
    }
    setTranscription(data.transcription || '');
  };

  const handlePDFExport = async () => {
    if (!transcription) return alert("Please upload or record audio first.");
    const res = await fetch('http://localhost:8000/export/pdf', {
      method: 'POST',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify({ prompt: transcription })
    });
    const data = await res.json();
    if (data.file) window.open('http://localhost:8000/' + data.file, '_blank');
    else alert("PDF generation failed.");
  };

  const stopRecording = async () => {
    const [buffer, blob] = await recorder.stop().getMp3();
    const file = new File([blob], "mic_recording.mp3", { type: "audio/mp3" });
    const url = URL.createObjectURL(blob);
    setAudioUrl(url);
    setIsRecording(false);
    setRecordingSeconds(30);
    clearInterval(intervalRef.current);
    const formData = new FormData();
    formData.append("audio", file);
    const res = await fetch("http://localhost:8000/transcribe", {
      method: "POST",
      body: formData
    });
    const data = await res.json();
    if (!data.transcription || data.transcription.includes("No voice detected")) {
      alert("No voice detected in the audio.");
    }
    setTranscription(data.transcription || '');
  };

  const handleMicClick = async () => {
    if (!isRecording) {
      try {
        await recorder.start();
        setIsRecording(true);
        intervalRef.current = setInterval(() => {
          setRecordingSeconds(prev => {
            if (prev <= 1) {
              stopRecording();
              return 0;
            }
            return prev - 1;
          });
        }, 1000);
      } catch (err) {
        console.error("Mic error:", err);
        alert("Microphone access denied.");
      }
    } else {
      stopRecording();
    }
  };

  return (
    <div className="flex h-screen w-screen bg-gradient-to-br from-zinc-900 to-gray-800 text-white p-2">
      {/* Left Panel */}
      <div className="w-1/5 flex flex-col items-center bg-zinc-800 rounded-2xl p-4 shadow-md">
        <img src="/logo.png" alt="Logo" className="h-16 mb-4" />
        <button className="text-xl hover:text-teal-400 mb-3">
          <label>
            <FaUpload />
            <input type="file" accept=".mp3,.wav" onChange={handleFileUpload} hidden />
          </label>
        </button>
        <button onClick={handleMicClick} className="text-xl hover:text-teal-400 mb-3">
          <FaMicrophone />
        </button>
        <button onClick={handlePDFExport} className="text-xl hover:text-teal-400">
          <FaFilePdf />
        </button>
        <img src="/call_center.jpg" alt="Call Center" className="h-32 mt-auto" />
      </div>

      {/* Center Chat Panel */}
      <div className="w-3/5 mx-4 flex flex-col rounded-2xl bg-zinc-900 shadow-md">
        <h1 className="text-center text-2xl font-bold border-b border-zinc-700 py-3">AI Call Summarization Chatbot</h1>
        <div className="flex-1 overflow-y-auto p-4" ref={chatWindowRef}>
          {messages.map((msg, i) => (
            <div key={i} className={`mb-3 flex ${msg.sender === 'user' ? 'justify-end' : 'justify-start'}`}>
              <div className="flex items-center space-x-2">
                <span>{msg.sender === 'user' ? 'ðŸ§‘' : 'ðŸ¤–'}</span>
                <span className="bg-zinc-700 p-2 rounded-xl max-w-[75%] break-words">{msg.text}</span>
              </div>
            </div>
          ))}
        </div>
        <div className="border-t border-zinc-700 p-3">
          <input
            type="text"
            className="w-full p-2 bg-zinc-800 rounded-xl focus:outline-none"
            value={input}
            onChange={e => setInput(e.target.value)}
            onKeyDown={e => e.key === 'Enter' && handleSend()}
            placeholder="Type your question..."
          />
        </div>
      </div>

      {/* Right Panel */}
      <div className="w-1/5 bg-zinc-800 rounded-2xl p-4 flex flex-col">
        <div className="h-20 mb-4 border border-zinc-700 rounded-xl flex items-center justify-center text-zinc-400 text-sm">
          ðŸŽµ Voice waveform
        </div>
        <div className="flex-1 flex flex-col">
          <label className="mb-1 font-semibold">Transcript Preview</label>
          <textarea
            className="flex-1 resize-none p-2 rounded-xl bg-zinc-900 text-white border border-zinc-700 overflow-y-auto"
            value={transcription}
            readOnly
          />
        </div>
      </div>
    </div>
  );
}

export default App;
