import React, { useState, useEffect, useRef } from 'react';
import './App.css';
import MicRecorder from 'mic-recorder-to-mp3';
import { FaMicrophone, FaFilePdf, FaUpload, FaUserCircle, FaRobot } from 'react-icons/fa';
import WaveSurfer from 'wavesurfer.js';

const recorder = new MicRecorder({ bitRate: 128 });

function App() {
  const [messages, setMessages] = useState([]);
  const [input, setInput] = useState('');
  const [transcription, setTranscription] = useState('');
  const [isRecording, setIsRecording] = useState(false);
  const [audioUrl, setAudioUrl] = useState('');
  const [recordingSeconds, setRecordingSeconds] = useState(30);
  const intervalRef = useRef(null);
  const chatRef = useRef(null);
  const waveformRef = useRef(null);
  const waveInstanceRef = useRef(null);

  useEffect(() => {
    chatRef.current?.scrollTo(0, chatRef.current.scrollHeight);
  }, [messages]);

  const setupWaveform = (url) => {
    if (waveInstanceRef.current) {
      waveInstanceRef.current.destroy();
    }
    const wave = WaveSurfer.create({
      container: waveformRef.current,
      waveColor: '#38bdf8',
      progressColor: '#0ea5e9',
      height: 60,
    });
    wave.load(url);
    waveInstanceRef.current = wave;
  };

  const handleSend = async () => {
    if (!input.trim() || !transcription) return;
    const userMessage = { text: input, sender: 'user' };
    setMessages(prev => [...prev, userMessage]);
    setInput('');

    const res = await fetch('http://localhost:8000/chat', {
      method: 'POST',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify({ prompt: input, context: transcription }),
    });
    const data = await res.json();
    setMessages(prev => [...prev, { text: data.response, sender: 'bot' }]);
  };

  const handleFileUpload = async (e) => {
    const file = e.target.files[0];
    if (!file) return;

    const formData = new FormData();
    formData.append('audio', file);

    const res = await fetch('http://localhost:8000/transcribe', {
      method: 'POST',
      body: formData,
    });
    const data = await res.json();
    if (data.transcription?.includes("No voice")) {
      alert("No voice detected in the audio.");
    }
    setTranscription(data.transcription || '');
    const url = URL.createObjectURL(file);
    setAudioUrl(url);
    setupWaveform(url);
  };

  const stopRecording = async () => {
    const [buffer, blob] = await recorder.stop().getMp3();
    const file = new File([blob], 'recorded.mp3', { type: 'audio/mp3' });
    const url = URL.createObjectURL(blob);
    setAudioUrl(url);
    setupWaveform(url);
    setIsRecording(false);
    setRecordingSeconds(30);
    clearInterval(intervalRef.current);

    const formData = new FormData();
    formData.append('audio', file);
    const res = await fetch('http://localhost:8000/transcribe', {
      method: 'POST',
      body: formData,
    });
    const data = await res.json();
    if (data.transcription?.includes("No voice")) {
      alert("No voice detected.");
    }
    setTranscription(data.transcription || '');
  };

  const handleMicClick = async () => {
    if (!isRecording) {
      try {
        await recorder.start();
        setIsRecording(true);
        intervalRef.current = setInterval(() => {
          setRecordingSeconds(prev => {
            if (prev <= 1) {
              stopRecording();
              return 0;
            }
            return prev - 1;
          });
        }, 1000);
      } catch (err) {
        alert('Microphone permission denied.');
      }
    } else {
      stopRecording();
    }
  };

  const handleExportPDF = async () => {
    if (!transcription) return alert('No transcription available.');
    const res = await fetch('http://localhost:8000/export/pdf', {
      method: 'POST',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify({ prompt: transcription }),
    });
    const data = await res.json();
    if (data?.file) {
      window.open(`http://localhost:8000${data.file}`, '_blank');
    } else {
      alert('PDF generation failed.');
    }
  };

  return (
    <div className="app-container">
      {/* Left */}
      <div className="panel left-panel">
        <img src="/logo.png" alt="Logo" className="logo-img" />
        <div className="panel-button">
          <label>
            <FaUpload size={24} />
            <input type="file" hidden onChange={handleFileUpload} />
            <span>Upload</span>
          </label>
        </div>
        <div className="panel-button" onClick={handleMicClick}>
          <FaMicrophone size={24} />
          <span>Microphone</span>
        </div>
        <div className="panel-button" onClick={handleExportPDF}>
          <FaFilePdf size={24} />
          <span>Export PDF</span>
        </div>
        <div className="guide">
          <h3>How to Use</h3>
          <ul>
            <li>1. Upload or record audio</li>
            <li>2. Ask questions</li>
            <li>3. Export as PDF</li>
          </ul>
        </div>
        <img src="/call_center.jpg" className="call-img" alt="Call center" />
      </div>

      {/* Center */}
      <div className="panel chat-panel">
        <div className="chat-header">AI Call Summarization Chatbot</div>
        <div className="chat-box" ref={chatRef}>
          {messages.map((msg, i) => (
            <div key={i} className={`chat-msg ${msg.sender}`}>
              {msg.sender === 'user' ? 'üßë‚Äçüíº' : 'ü§ñ'} <span>{msg.text}</span>
            </div>
          ))}
        </div>
        <div className="chat-input">
          <input
            type="text"
            value={input}
            onChange={(e) => setInput(e.target.value)}
            placeholder="Ask something..."
            onKeyDown={(e) => e.key === 'Enter' && handleSend()}
          />
        </div>
      </div>

      {/* Right */}
      <div className="panel right-panel">
        <div className="waveform" ref={waveformRef}>üéµ Loading waveform...</div>
        <div className="transcript-section">
          <h3>Transcript Preview</h3>
          <textarea className="transcript-box" value={transcription} readOnly />
        </div>
      </div>
    </div>
  );
}

export default App;
