import React, { useState, useEffect, useRef } from 'react';
import './App.css';
import MicRecorder from 'mic-recorder-to-mp3';
import { FaUserCircle, FaRobot, FaMicrophone, FaFilePdf, FaUpload } from 'react-icons/fa';

const recorder = new MicRecorder({ bitRate: 128 });

function App() {
  const [messages, setMessages] = useState([]);
  const [input, setInput] = useState('');
  const [transcription, setTranscription] = useState('');
  const [isRecording, setIsRecording] = useState(false);
  const [recordingSeconds, setRecordingSeconds] = useState(30);
  const [audioUrl, setAudioUrl] = useState('');
  const [isTyping, setIsTyping] = useState(false);
  const intervalRef = useRef(null);
  const chatWindowRef = useRef(null);

  useEffect(() => {
    if (chatWindowRef.current) {
      chatWindowRef.current.scrollTop = chatWindowRef.current.scrollHeight;
    }
  }, [messages]);

  const animateBotReply = (fullText) => {
    let i = 0;
    const botMessage = { text: '', sender: 'bot' };
    setMessages(prev => [...prev.slice(0, -1), botMessage]);

    const interval = setInterval(() => {
      i++;
      if (i > fullText.length) {
        clearInterval(interval);
        setIsTyping(false);
        return;
      }
      setMessages(prev => {
        const updated = [...prev];
        updated[updated.length - 1] = { ...botMessage, text: fullText.slice(0, i) };
        return updated;
      });
    }, 30);
  };

  const handleSend = async () => {
    if (!input.trim() || !transcription) return;
    const userMessage = { text: input, sender: 'user' };
    setMessages(prev => [...prev, userMessage]);
    setInput('');

    setIsTyping(true);
    setMessages(prev => [...prev, { text: "Typing...", sender: "bot" }]);

    try {
      const res = await fetch('http://localhost:8000/chat', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ prompt: input, context: transcription })
      });
      const data = await res.json();
      animateBotReply(data.response);
    } catch (err) {
      console.error(err);
      setIsTyping(false);
      setMessages(prev => [...prev, { text: "‚ö†Ô∏è Failed to get response", sender: "bot" }]);
    }
  };

  const handleFileUpload = async (e) => {
    const file = e.target.files[0];
    if (!file) return;
    const formData = new FormData();
    formData.append('audio', file);
    const res = await fetch('http://localhost:8000/transcribe', {
      method: 'POST',
      body: formData
    });
    const data = await res.json();
    if (!data.transcription || data.transcription.includes("No voice detected")) {
      alert("No voice detected in the audio.");
    }
    setTranscription(data.transcription || '');
  };

  const handlePDFExport = async () => {
    if (!transcription) return alert("Please upload or record audio first.");
    const res = await fetch('http://localhost:8000/export/pdf', {
      method: 'POST',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify({ prompt: transcription })
    });
    const data = await res.json();
    if (data.file) window.open('http://localhost:8000/' + data.file, '_blank');
    else alert("PDF generation failed.");
  };

  const stopRecording = async () => {
    const [buffer, blob] = await recorder.stop().getMp3();
    const file = new File([blob], "mic_recording.mp3", { type: "audio/mp3" });
    const url = URL.createObjectURL(blob);
    setAudioUrl(url);
    setIsRecording(false);
    setRecordingSeconds(30);
    clearInterval(intervalRef.current);
    const formData = new FormData();
    formData.append("audio", file);
    const res = await fetch("http://localhost:8000/transcribe", {
      method: "POST",
      body: formData
    });
    const data = await res.json();
    if (!data.transcription || data.transcription.includes("No voice detected")) {
      alert("No voice detected in the audio.");
    }
    setTranscription(data.transcription || '');
  };

  const handleMicClick = async () => {
    if (!isRecording) {
      try {
        await recorder.start();
        setIsRecording(true);
        intervalRef.current = setInterval(() => {
          setRecordingSeconds(prev => {
            if (prev <= 1) {
              stopRecording();
              return 0;
            }
            return prev - 1;
          });
        }, 1000);
      } catch (err) {
        console.error("Mic error:", err);
        alert("Microphone access denied.");
      }
    } else {
      stopRecording();
    }
  };

  return (
    <div className="app">
      {/* Left Panel */}
      <div className="left-panel">
        <h2 className="text-lg font-bold mb-2 text-center text-white">AI Call Summarizer</h2>
        <img src="/logo192.png" alt="Logo" className="w-24 h-24 mx-auto mb-4 rounded-full" />
        <label className="icon-button">
          <FaUpload />
          <input type="file" accept=".mp3,.wav" onChange={handleFileUpload} hidden />
        </label>
        <button className="icon-button" onClick={handleMicClick}>
          <FaMicrophone />
        </button>
        <button className="icon-button" onClick={handlePDFExport}>
          <FaFilePdf />
        </button>
        <img src="/call_center.jpg" alt="Call Center" className="mt-4 rounded-xl w-full" />
      </div>

      {/* Center Chat Panel */}
      <div className="chat-panel">
        <div className="chat-title">AI Call Summarization Chatbot</div>
        <div className="chat-window" ref={chatWindowRef}>
          {messages.map((msg, i) => (
            <div key={i} className={`chat-message ${msg.sender}`}>
              {msg.sender === 'user' ? 'üßë‚Äçüíº' : 'ü§ñ'} <span>{msg.text}</span>
            </div>
          ))}
          {isTyping && <div className="chat-message bot"><span className="typing-indicator">Typing...</span></div>}
        </div>
        <div className="chat-input">
          <input
            type="text"
            value={input}
            onChange={e => setInput(e.target.value)}
            onKeyDown={e => e.key === 'Enter' && handleSend()}
            placeholder="Ask me anything..."
          />
        </div>
      </div>

      {/* Right Panel */}
      <div className="right-panel">
        <div className="waveform-placeholder">üéµ Voice waveform preview</div>
        <div className="transcription-box">
          <strong className="text-white">Transcript Preview</strong>
          <textarea
            className="scrollable-box resize-y"
            value={transcription}
            readOnly
          />
        </div>
      </div>
    </div>
  );
}

export default App;
