import React, { useState, useEffect, useRef } from 'react';
import './App.css';
import MicRecorder from 'mic-recorder-to-mp3';
import { FaUserCircle, FaRobot, FaMicrophone, FaFilePdf, FaUpload } from 'react-icons/fa';

const recorder = new MicRecorder({ bitRate: 128 });

function App() {
  const [messages, setMessages] = useState([]);
  const [input, setInput] = useState('');
  const [transcription, setTranscription] = useState('');
  const [isRecording, setIsRecording] = useState(false);
  const [recordingSeconds, setRecordingSeconds] = useState(30);
  const [audioUrl, setAudioUrl] = useState('');
  const [botTyping, setBotTyping] = useState(false);
  const intervalRef = useRef(null);
  const chatWindowRef = useRef(null);

  useEffect(() => {
    if (chatWindowRef.current) {
      chatWindowRef.current.scrollTop = chatWindowRef.current.scrollHeight;
    }
  }, [messages]);

  const handleSend = async () => {
    if (!input.trim() || !transcription) return;
    const userMessage = { text: input, sender: 'user' };
    setMessages(prev => [...prev, userMessage]);
    setInput('');
    setBotTyping(true);

    const res = await fetch('http://localhost:8000/chat', {
      method: 'POST',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify({ prompt: input, context: transcription })
    });
    const data = await res.json();
    setMessages(prev => [...prev, { text: data.response, sender: 'bot' }]);
    setBotTyping(false);
  };

  const handleFileUpload = async (e) => {
    const file = e.target.files[0];
    if (!file) return;
    const formData = new FormData();
    formData.append('audio', file);
    const res = await fetch('http://localhost:8000/transcribe', {
      method: 'POST',
      body: formData
    });
    const data = await res.json();
    if (!data.transcription || data.transcription.includes("No voice detected")) {
      alert("No voice detected in the audio.");
    }
    setTranscription(data.transcription || '');
  };

  const handlePDFExport = async () => {
    if (!transcription) return alert("Please upload or record audio first.");
    const res = await fetch('http://localhost:8000/export/pdf', {
      method: 'POST',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify({ prompt: transcription })
    });
    const data = await res.json();
    if (data.file) window.open('http://localhost:8000/' + data.file, '_blank');
    else alert("PDF generation failed.");
  };

  const stopRecording = async () => {
    const [buffer, blob] = await recorder.stop().getMp3();
    const file = new File([blob], "mic_recording.mp3", { type: "audio/mp3" });
    const url = URL.createObjectURL(blob);
    setAudioUrl(url);
    setIsRecording(false);
    setRecordingSeconds(30);
    clearInterval(intervalRef.current);
    const formData = new FormData();
    formData.append("audio", file);
    const res = await fetch("http://localhost:8000/transcribe", {
      method: "POST",
      body: formData
    });
    const data = await res.json();
    if (!data.transcription || data.transcription.includes("No voice detected")) {
      alert("No voice detected in the audio.");
    }
    setTranscription(data.transcription || '');
  };

  const handleMicClick = async () => {
    if (!isRecording) {
      try {
        await recorder.start();
        setIsRecording(true);
        intervalRef.current = setInterval(() => {
          setRecordingSeconds(prev => {
            if (prev <= 1) {
              stopRecording();
              return 0;
            }
            return prev - 1;
          });
        }, 1000);
      } catch (err) {
        console.error("Mic error:", err);
        alert("Microphone access denied.");
      }
    } else {
      stopRecording();
    }
  };

  return (
    <div className="app">
      {/* Left Panel */}
      <div className="left-panel">
        <img src="/logo192.png" alt="Logo" className="logo" />
        <div className="icon-button">
          <label>
            <FaUpload />
            <input type="file" accept=".mp3,.wav" onChange={handleFileUpload} hidden />
          </label>
          <div className="icon-label">Upload</div>
        </div>
        <div className="icon-button" onClick={handleMicClick}>
          <FaMicrophone />
          <div className="icon-label">Mic</div>
        </div>
        <div className="icon-button" onClick={handlePDFExport}>
          <FaFilePdf />
          <div className="icon-label">Export PDF</div>
        </div>

        <div className="how-to-box">
          <h4>How to Use</h4>
          <ul>
            <li>üéôÔ∏è Upload or record audio</li>
            <li>ü§ñ Chat with transcript</li>
            <li>üìÑ Export as PDF</li>
          </ul>
        </div>
      </div>

      {/* Center Chat Panel */}
      <div className="chat-panel">
        <div className="chat-title">AI Call Summarization Chatbot</div>
        <div className="chat-window" ref={chatWindowRef}>
          {messages.map((msg, i) => (
            <div key={i} className={`chat-message ${msg.sender}`}>
              {msg.sender === 'user' ? <FaUserCircle /> : <FaRobot />}
              <span>{msg.sender === 'user' ? 'üôÇ ' : 'ü§ñ '}{msg.text}</span>
            </div>
          ))}
          {botTyping && (
            <div className="typing-indicator">ü§ñ Typing...</div>
          )}
        </div>
        <div className="chat-input">
          <input
            type="text"
            value={input}
            onChange={e => setInput(e.target.value)}
            onKeyDown={e => e.key === 'Enter' && handleSend()}
            placeholder="Ask a question..."
          />
        </div>
      </div>

      {/* Right Panel */}
      <div className="right-panel">
        <div className="waveform-placeholder">üéµ Live waveform (placeholder)</div>
        {audioUrl && <audio controls src={audioUrl} />}
        <div className="transcription-box">
          <strong>Transcript Preview</strong>
          <textarea
            className="scrollable-box"
            value={transcription}
            readOnly
          />
        </div>
      </div>
    </div>
  );
}

export default App;
