import React, { useState, useRef } from 'react';
import './App.css';
import { setupSilenceDetection } from './audioUtils';

function App() {
  const [theme, setTheme] = useState('dark');
  const [chatLog, setChatLog] = useState([]);
  const [transcript, setTranscript] = useState('');
  const [isRecording, setIsRecording] = useState(false);
  const mediaRecorderRef = useRef(null);
  const chunksRef = useRef([]);

  const toggleTheme = () => {
    const newTheme = theme === 'dark' ? 'light' : 'dark';
    setTheme(newTheme);
    document.body.className = newTheme;
  };

  const addToChat = (sender, text) => {
    setChatLog(prev => [...prev, {
      sender,
      text,
      timestamp: new Date().toLocaleTimeString()
    }]);
  };

  const startRecording = async () => {
    const stream = await navigator.mediaDevices.getUserMedia({ audio: true });
    const recorder = new MediaRecorder(stream);
    mediaRecorderRef.current = recorder;
    chunksRef.current = [];

    recorder.ondataavailable = (e) => {
      if (e.data.size > 0) chunksRef.current.push(e.data);
    };

    recorder.onstop = async () => {
      const audioBlob = new Blob(chunksRef.current, { type: 'audio/wav' });
      const formData = new FormData();
      formData.append('file', audioBlob, 'recording.wav');

      const response = await fetch('http://localhost:8000/transcribe', {
        method: 'POST',
        body: formData
      });

      const result = await response.json();
      const text = result.transcript || 'No transcription';
      setTranscript(text);
      addToChat('User', text);

      const aiRes = await fetch('http://localhost:8000/chat', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ prompt: text })
      });

      const { response: reply } = await aiRes.json();
      addToChat('Bot', reply);
    };

    recorder.start();
    setIsRecording(true);

    setupSilenceDetection(stream, () => {
      recorder.stop();
      setIsRecording(false);
    });
  };

  const exportChat = async (type) => {
    const res = await fetch(`http://localhost:8000/export/${type}`, {
      method: 'POST',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify({ chat: chatLog })
    });
    const blob = await res.blob();
    const a = document.createElement('a');
    a.href = URL.createObjectURL(blob);
    a.download = `chat.${type === 'pdf' ? 'pdf' : 'docx'}`;
    a.click();
  };

  return (
    <div className={`app-container ${theme}`}>
      <div className="sidebar">
        <img src="logo.png" alt="Bank Logo" className="logo" />
        <button onClick={startRecording}>🎤 Microphone</button>
        <button onClick={toggleTheme}>🌓 {theme === 'dark' ? 'Light' : 'Dark'} Mode</button>
        <button onClick={() => exportChat('pdf')}>📄 Export PDF</button>
        <button onClick={() => exportChat('word')}>📃 Export Word</button>
        <div className="how-to">
          <h3>📌 How to use:</h3>
          <p>Upload or record audio<br />Interact via chat<br />Export summary</p>
        </div>
        <img src="chat_image.png" alt="Chat illustration" className="footer-image" />
      </div>

      <div className="main-panel">
        <h2 className="title">AI Call Summarization Chatbot</h2>
        <div className="chat-box">
          {chatLog.map((msg, i) => (
            <div key={i} className={`chat-msg ${msg.sender.toLowerCase()}`}>
              <strong>{msg.sender}:</strong> {msg.text}
              <span className="timestamp">{msg.timestamp}</span>
            </div>
          ))}
        </div>
        <div className="transcript-box">
          <p>📝 Transcript Preview:</p>
          <textarea value={transcript} readOnly />
        </div>
      </div>
    </div>
  );
}

export default App;
