import React, { useState, useRef, useEffect } from 'react';
import './App.css';
import { setupSilenceDetection } from './audioUtils';

function App() {
  const [theme, setTheme] = useState('light');
  const [chatLog, setChatLog] = useState([]);
  const [transcript, setTranscript] = useState('');
  const [isRecording, setIsRecording] = useState(false);
  const mediaRecorderRef = useRef(null);
  const chunksRef = useRef([]);

  const toggleTheme = () => {
    setTheme(prev => prev === 'light' ? 'dark' : 'light');
    document.body.className = theme === 'light' ? 'dark' : 'light';
  };

  const startRecording = async () => {
    const stream = await navigator.mediaDevices.getUserMedia({ audio: true });
    const mediaRecorder = new MediaRecorder(stream);
    mediaRecorderRef.current = mediaRecorder;
    chunksRef.current = [];

    mediaRecorder.ondataavailable = e => {
      if (e.data.size > 0) chunksRef.current.push(e.data);
    };

    mediaRecorder.onstop = async () => {
      const blob = new Blob(chunksRef.current, { type: 'audio/wav' });
      const formData = new FormData();
      formData.append('file', blob, 'recording.wav');

      const res = await fetch('http://localhost:8000/transcribe', {
        method: 'POST',
        body: formData
      });
      const data = await res.json();
      setTranscript(data.transcript || 'Transcription failed');
      addToChat('User', data.transcript || '...');

      const chatRes = await fetch('http://localhost:8000/chat', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ prompt: data.transcript })
      });
      const chatData = await chatRes.json();
      addToChat('Bot', chatData.response || '...');
    };

    mediaRecorder.start();
    setIsRecording(true);

    setupSilenceDetection(stream, () => {
      mediaRecorder.stop();
      setIsRecording(false);
    });
  };

  const addToChat = (sender, text) => {
    setChatLog(prev => [...prev, { sender, text, timestamp: new Date().toLocaleTimeString() }]);
  };

  const exportChat = async (type) => {
    const res = await fetch(`http://localhost:8000/export/${type}`, {
      method: 'POST',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify({ chat: chatLog })
    });
    const blob = await res.blob();
    const url = window.URL.createObjectURL(blob);
    const a = document.createElement('a');
    a.href = url;
    a.download = `chat.${type === 'pdf' ? 'pdf' : 'docx'}`;
    a.click();
  };

  return (
    <div className={`app ${theme}`}>
      <header className="app-header">
        <h1>üéß AI Call Summarizer</h1>
        <div className="controls">
          <button onClick={startRecording} disabled={isRecording}>
            {isRecording ? 'Listening...' : 'üéôÔ∏è Start Mic'}
          </button>
          <button onClick={toggleTheme}>
            Toggle {theme === 'light' ? 'üåô Dark' : '‚òÄÔ∏è Light'}
          </button>
          <button onClick={() => exportChat('pdf')}>Export PDF</button>
          <button onClick={() => exportChat('word')}>Export Word</button>
        </div>
      </header>

      <div className="chat-window">
        {chatLog.map((msg, i) => (
          <div key={i} className={`chat-msg ${msg.sender.toLowerCase()}`}>
            <strong>{msg.sender}:</strong> {msg.text}
            <span className="timestamp">{msg.timestamp}</span>
          </div>
        ))}
      </div>
    </div>
  );
}

export default App;
