import React, { useState, useEffect, useRef } from 'react';
import MicRecorder from 'mic-recorder-to-mp3';
import {
  FaMicrophone,
  FaFilePdf,
  FaUpload,
  FaUserCircle,
  FaRobot
} from 'react-icons/fa';

const recorder = new MicRecorder({ bitRate: 128 });

function App() {
  const [messages, setMessages] = useState([]);
  const [input, setInput] = useState('');
  const [transcription, setTranscription] = useState('');
  const [isRecording, setIsRecording] = useState(false);
  const [recordingSeconds, setRecordingSeconds] = useState(30);
  const [audioUrl, setAudioUrl] = useState('');
  const intervalRef = useRef(null);
  const chatWindowRef = useRef(null);
  const [isBotTyping, setIsBotTyping] = useState(false);

  useEffect(() => {
    if (chatWindowRef.current) {
      chatWindowRef.current.scrollTop = chatWindowRef.current.scrollHeight;
    }
  }, [messages]);

  const handleSend = async () => {
    if (!input.trim() || !transcription) return;
    const userMessage = { text: input, sender: 'user' };
    setMessages(prev => [...prev, userMessage]);
    setInput('');
    setIsBotTyping(true);

    const res = await fetch('http://localhost:8000/chat', {
      method: 'POST',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify({ prompt: input, context: transcription })
    });
    const data = await res.json();
    setMessages(prev => [...prev, { text: data.response, sender: 'bot' }]);
    setIsBotTyping(false);
  };

  const handleFileUpload = async (e) => {
    const file = e.target.files[0];
    if (!file) return;
    const formData = new FormData();
    formData.append('audio', file);
    const res = await fetch('http://localhost:8000/transcribe', {
      method: 'POST',
      body: formData
    });
    const data = await res.json();
    if (!data.transcription || data.transcription.includes("No voice detected")) {
      alert("No voice detected in the audio.");
    }
    setTranscription(data.transcription || '');
  };

  const handlePDFExport = async () => {
    if (!transcription) return alert("Please upload or record audio first.");
    const res = await fetch('http://localhost:8000/export/pdf', {
      method: 'POST',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify({ prompt: transcription })
    });
    const data = await res.json();
    if (data.file) window.open('http://localhost:8000/' + data.file, '_blank');
    else alert("PDF generation failed.");
  };

  const stopRecording = async () => {
    const [buffer, blob] = await recorder.stop().getMp3();
    const file = new File([blob], "mic_recording.mp3", { type: "audio/mp3" });
    const url = URL.createObjectURL(blob);
    setAudioUrl(url);
    setIsRecording(false);
    setRecordingSeconds(30);
    clearInterval(intervalRef.current);
    const formData = new FormData();
    formData.append("audio", file);
    const res = await fetch("http://localhost:8000/transcribe", {
      method: "POST",
      body: formData
    });
    const data = await res.json();
    if (!data.transcription || data.transcription.includes("No voice detected")) {
      alert("No voice detected in the audio.");
    }
    setTranscription(data.transcription || '');
  };

  const handleMicClick = async () => {
    if (!isRecording) {
      try {
        await recorder.start();
        setIsRecording(true);
        intervalRef.current = setInterval(() => {
          setRecordingSeconds(prev => {
            if (prev <= 1) {
              stopRecording();
              return 0;
            }
            return prev - 1;
          });
        }, 1000);
      } catch (err) {
        console.error("Mic error:", err);
        alert("Microphone access denied.");
      }
    } else {
      stopRecording();
    }
  };

  return (
    <div className="flex h-screen bg-primary text-white font-sans">
      {/* Left Panel */}
      <div className="w-1/5 p-4 bg-panel border border-white/10 rounded-box flex flex-col items-center justify-between">
        <div className="flex flex-col gap-4 items-center">
          <img src="/logo.png" alt="Logo" className="w-20 h-20 rounded-full border" />
          <button className="w-full bg-accent py-2 rounded-box text-white font-bold">
            <label className="cursor-pointer">
              <FaUpload className="inline mr-2" />
              Upload
              <input type="file" accept=".mp3,.wav" onChange={handleFileUpload} hidden />
            </label>
          </button>
          <button
            className="w-full bg-accent py-2 rounded-box text-white font-bold"
            onClick={handleMicClick}
          >
            <FaMicrophone className="inline mr-2" />
            {isRecording ? `Stop (${recordingSeconds}s)` : "Record"}
          </button>
          <button
            className="w-full bg-accent py-2 rounded-box text-white font-bold"
            onClick={handlePDFExport}
          >
            <FaFilePdf className="inline mr-2" /> Export PDF
          </button>
        </div>
        <img src="/call_center.jpg" alt="Call Center" className="w-full h-32 object-cover rounded-box" />
      </div>

      {/* Chat Panel */}
      <div className="w-3/5 p-4 bg-panel border border-white/10 mx-2 rounded-box flex flex-col">
        <div className="text-center text-xl font-bold mb-2">AI Call Summarization Chatbot</div>
        <div className="flex-1 overflow-y-auto space-y-4 px-2" ref={chatWindowRef}>
          {messages.map((msg, i) => (
            <div key={i} className={`flex items-start gap-2 ${msg.sender === 'user' ? 'justify-end' : 'justify-start'}`}>
              {msg.sender === 'user' ? (
                <div className="bg-chatUser p-2 rounded-lg max-w-md text-black">ðŸ§‘ {msg.text}</div>
              ) : (
                <div className="bg-chatBot p-2 rounded-lg max-w-md text-black">ðŸ¤– {msg.text}</div>
              )}
            </div>
          ))}
          {isBotTyping && <div className="italic text-gray-400">ðŸ¤– Typing...</div>}
        </div>
        <div className="mt-4">
          <input
            type="text"
            value={input}
            onChange={e => setInput(e.target.value)}
            onKeyDown={e => e.key === 'Enter' && handleSend()}
            placeholder="Type your query..."
            className="w-full p-3 rounded-lg bg-gray-800 text-white focus:outline-none"
          />
        </div>
      </div>

      {/* Right Panel */}
      <div className="w-1/5 p-4 bg-panel border border-white/10 rounded-box flex flex-col items-stretch">
        <div className="text-sm mb-2 text-accent font-bold">ðŸŽµ Audio Visualization</div>
        <div className="h-24 mb-4 border border-dashed border-gray-500 rounded-box flex items-center justify-center text-gray-400 italic">
          {isRecording ? "Listening..." : "Waveform preview"}
        </div>
        {audioUrl && (
          <audio controls src={audioUrl} className="mb-2" />
        )}
        <div className="text-sm font-bold mb-1 text-accent">Transcript Preview</div>
        <textarea
          value={transcription}
          readOnly
          className="scrollable-box bg-black text-white p-2 rounded-box h-56 resize-none overflow-y-auto border border-white/20"
        />
      </div>
    </div>
  );
}

export default App;
