import React, { useState, useEffect, useRef } from 'react';
import axios from 'axios';
import './App.css';
import { setupSilenceDetection } from './audioUtils';

function App() {
  const [isRecording, setIsRecording] = useState(false);
  const [chatLog, setChatLog] = useState([]);
  const [theme, setTheme] = useState('dark');
  const [transcript, setTranscript] = useState('');
  const mediaRecorderRef = useRef(null);
  const audioChunks = useRef([]);

  const toggleTheme = () => {
    setTheme(prev => (prev === 'dark' ? 'light' : 'dark'));
  };

  useEffect(() => {
    document.body.className = theme;
  }, [theme]);

  const handleUpload = async (e) => {
    const file = e.target.files[0];
    if (!file) return;
    const formData = new FormData();
    formData.append('file', file);
    const { data } = await axios.post('http://localhost:8000/transcribe', formData);
    setTranscript(data.transcript);
    setChatLog(log => [...log, { type: 'user', text: data.transcript }]);
    queryLLM(data.transcript);
  };

  const handleMicStart = async () => {
    const stream = await navigator.mediaDevices.getUserMedia({ audio: true });
    const mediaRecorder = new MediaRecorder(stream);
    mediaRecorderRef.current = mediaRecorder;
    audioChunks.current = [];
    mediaRecorder.ondataavailable = event => audioChunks.current.push(event.data);
    mediaRecorder.onstop = async () => {
      const audioBlob = new Blob(audioChunks.current, { type: 'audio/webm' });
      const formData = new FormData();
      formData.append('file', audioBlob);
      const { data } = await axios.post('http://localhost:8000/transcribe', formData);
      setTranscript(data.transcript);
      setChatLog(log => [...log, { type: 'user', text: data.transcript }]);
      queryLLM(data.transcript);
    };
    mediaRecorder.start();
    setIsRecording(true);
    setupSilenceDetection(mediaRecorder, stream, () => {
      mediaRecorder.stop();
      setIsRecording(false);
    });
  };

  const queryLLM = async (text) => {
    const { data } = await axios.post('http://localhost:8000/chat', {
      query: text,
      session_id: 'local-session'
    });
    setChatLog(log => [...log, { type: 'bot', text: data.response }]);
  };

  return (
    <div className="app">
      <header>
        <h2>üéôÔ∏è AI Call Summarizer</h2>
        <button onClick={toggleTheme}>Toggle Theme</button>
      </header>
      <div className="controls">
        <label className="upload-btn">
          Upload Audio
          <input type="file" accept="audio/*" hidden onChange={handleUpload} />
        </label>
        <button onClick={handleMicStart}>{isRecording ? 'Recording...' : 'Start Mic'}</button>
        <button onClick={() => window.print()}>Export PDF</button>
        <button onClick={() => alert('Word export to be implemented')}>Export Word</button>
      </div>
      <div className="main">
        <div className="chat">
          {chatLog.map((entry, i) => (
            <div key={i} className={`chat-bubble ${entry.type}`}>
              {entry.text}
            </div>
          ))}
        </div>
        <div className="transcript-box">
          <h4>Transcript Preview</h4>
          <pre>{transcript}</pre>
        </div>
      </div>
    </div>
  );
}

export default App;
