import React, { useState, useRef, useEffect } from "react";
import WaveSurfer from "wavesurfer.js";
import {
  startMicRecording,
  stopMicRecording,
  setupSilenceDetection,
} from "./audioUtils";
import "./App.css";
import { v4 as uuidv4 } from "uuid";

const App = () => {
  const [sessionId] = useState(uuidv4());
  const [transcription, setTranscription] = useState("");
  const [fileName, setFileName] = useState("");
  const [audioUrl, setAudioUrl] = useState("");
  const waveformRef = useRef(null);
  const wavesurfer = useRef(null);
  const [isRecording, setIsRecording] = useState(false);
  const [messages, setMessages] = useState([]);
  const [userInput, setUserInput] = useState("");
  const [isTyping, setIsTyping] = useState(false);

  useEffect(() => {
    if (waveformRef.current && audioUrl) {
      if (wavesurfer.current) {
        wavesurfer.current.destroy();
      }
      wavesurfer.current = WaveSurfer.create({
        container: waveformRef.current,
        waveColor: "#ccc",
        progressColor: "#4f46e5",
        height: 60,
      });
      wavesurfer.current.load(audioUrl);
    }
  }, [audioUrl]);

  const handleFileUpload = async (e) => {
    const file = e.target.files[0];
    setFileName(file.name);
    const formData = new FormData();
    formData.append("file", file); // fixed field name

    const response = await fetch("http://localhost:8000/transcribe", {
      method: "POST",
      body: formData,
    });

    const data = await response.json();
    setTranscription(data.transcription);
    setAudioUrl(URL.createObjectURL(file));
  };

  const handleMicRecord = async () => {
    setIsRecording(true);
    startMicRecording();
    setupSilenceDetection(stopRecording);
    setTimeout(stopRecording, 30000); // auto-stop after 30s
  };

  const stopRecording = async () => {
    const mp3Blob = await stopMicRecording();
    const audioURL = URL.createObjectURL(mp3Blob);
    setAudioUrl(audioURL);

    const formData = new FormData();
    formData.append("file", mp3Blob, "recording.mp3");

    const response = await fetch("http://localhost:8000/transcribe", {
      method: "POST",
      body: formData,
    });
    const data = await response.json();
    setTranscription(data.transcription);

    if (data.transcription) {
      const userMessage = { text: data.transcription, sender: "user" };
      setMessages((prev) => [...prev, userMessage]);
      setIsTyping(true);

      const res = await fetch("http://localhost:8000/chat", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({
          prompt: data.transcription,
          context: data.transcription,
          session_id: sessionId,
        }),
      });
      const botData = await res.json();
      const botMessage = { text: botData.response, sender: "bot" };
      setMessages((prev) => [...prev, botMessage]);
      setIsTyping(false);
    }
    setIsRecording(false);
  };

  const handleUserInput = async () => {
    const userMessage = { text: userInput, sender: "user" };
    setMessages((prev) => [...prev, userMessage]);
    setIsTyping(true);

    const res = await fetch("http://localhost:8000/chat", {
      method: "POST",
      headers: { "Content-Type": "application/json" },
      body: JSON.stringify({
        prompt: userInput,
        context: transcription,
        session_id: sessionId,
      }),
    });
    const data = await res.json();
    const botMessage = { text: data.response, sender: "bot" };
    setMessages((prev) => [...prev, botMessage]);
    setIsTyping(false);
    setUserInput("");
  };

  const exportPDF = async () => {
    const response = await fetch("http://localhost:8000/export/pdf", {
      method: "POST",
      headers: { "Content-Type": "application/json" },
      body: JSON.stringify({ messages }),
    });
    const blob = await response.blob();
    const downloadUrl = URL.createObjectURL(blob);
    const link = document.createElement("a");
    link.href = downloadUrl;
    link.download = "summary.pdf";
    link.click();
  };

  return (
    <div className="app">
      <div className="left-panel">
        <h2>Call Summarizer</h2>
        <input type="file" accept="audio/*" onChange={handleFileUpload} />
        <button onClick={handleMicRecord} disabled={isRecording}>
          {isRecording ? "Recording..." : "üéôÔ∏è Record via Mic"}
        </button>
        <button onClick={exportPDF}>Export as PDF</button>
      </div>

      <div className="center-panel">
        <div className="chat-box">
          {messages.map((msg, idx) => (
            <div key={idx} className={`msg ${msg.sender}`}>
              <strong>{msg.sender === "user" ? "You" : "Bot"}:</strong>{" "}
              {msg.text}
            </div>
          ))}
          {isTyping && <div className="typing">Bot is typing...</div>}
        </div>
        <div className="chat-input">
          <input
            type="text"
            placeholder="Ask about the call..."
            value={userInput}
            onChange={(e) => setUserInput(e.target.value)}
          />
          <button onClick={handleUserInput}>Send</button>
        </div>
      </div>

      <div className="right-panel">
        <h3>Transcript Preview</h3>
        <pre className="transcript-box">{transcription}</pre>
        <div ref={waveformRef} className="waveform" />
      </div>
    </div>
  );
};

export default App;
