import React, { useState, useEffect, useRef } from 'react';
import MicRecorder from 'mic-recorder-to-mp3';
import WaveSurfer from 'wavesurfer.js';
import { FaUserCircle, FaRobot, FaMicrophone, FaFilePdf, FaUpload, FaMoon, FaSun } from 'react-icons/fa';
import './App.css';

const recorder = new MicRecorder({ bitRate: 128 });

function App() {
  const [messages, setMessages] = useState([]);
  const [input, setInput] = useState('');
  const [transcription, setTranscription] = useState('');
  const [isRecording, setIsRecording] = useState(false);
  const [recordingSeconds, setRecordingSeconds] = useState(30);
  const [audioUrl, setAudioUrl] = useState('');
  const [isDarkMode, setIsDarkMode] = useState(true);
  const [isTyping, setIsTyping] = useState(false);

  const chatWindowRef = useRef(null);
  const intervalRef = useRef(null);
  const waveformRef = useRef(null);
  const wavesurfer = useRef(null);

  useEffect(() => {
    if (chatWindowRef.current) {
      chatWindowRef.current.scrollTop = chatWindowRef.current.scrollHeight;
    }
  }, [messages]);

  useEffect(() => {
    if (audioUrl && waveformRef.current) {
      if (wavesurfer.current) wavesurfer.current.destroy();
      wavesurfer.current = WaveSurfer.create({
        container: waveformRef.current,
        waveColor: '#38bdf8',
        progressColor: '#0ea5e9',
        height: 60,
        barWidth: 2,
        responsive: true
      });
      wavesurfer.current.load(audioUrl);
    }
  }, [audioUrl]);

  const handleSend = async () => {
    if (!input.trim() || !transcription) return;
    const userMessage = { text: input, sender: 'user' };
    setMessages(prev => [...prev, userMessage]);
    setInput('');
    setIsTyping(true);

    const res = await fetch('http://localhost:8000/chat', {
      method: 'POST',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify({ prompt: input, context: transcription })
    });
    const data = await res.json();

    setTimeout(() => {
      setMessages(prev => [...prev, { text: data.response, sender: 'bot' }]);
      setIsTyping(false);
    }, 600);
  };

  const handleFileUpload = async (e) => {
    const file = e.target.files[0];
    if (!file) return;
    const formData = new FormData();
    formData.append('audio', file);
    const res = await fetch('http://localhost:8000/transcribe', {
      method: 'POST',
      body: formData
    });
    const data = await res.json();
    setTranscription(data.transcription || '');
    setAudioUrl(URL.createObjectURL(file));
  };

  const handlePDFExport = async () => {
    if (!transcription) return alert("Upload or record audio first.");
    const res = await fetch('http://localhost:8000/export/pdf', {
      method: 'POST',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify({ prompt: transcription })
    });
    const data = await res.json();
    if (data.file) window.open('http://localhost:8000/' + data.file, '_blank');
    else alert("PDF generation failed.");
  };

  const stopRecording = async () => {
    const [buffer, blob] = await recorder.stop().getMp3();
    const file = new File([blob], "mic_recording.mp3", { type: "audio/mp3" });
    const url = URL.createObjectURL(blob);
    setAudioUrl(url);
    setIsRecording(false);
    clearInterval(intervalRef.current);
    const formData = new FormData();
    formData.append("audio", file);
    const res = await fetch("http://localhost:8000/transcribe", {
      method: "POST",
      body: formData
    });
    const data = await res.json();
    setTranscription(data.transcription || '');
  };

  const handleMicClick = async () => {
    if (!isRecording) {
      await recorder.start();
      setIsRecording(true);
      setRecordingSeconds(30);
      intervalRef.current = setInterval(() => {
        setRecordingSeconds(prev => {
          if (prev <= 1) {
            stopRecording();
            return 0;
          }
          return prev - 1;
        });
      }, 1000);
    } else {
      stopRecording();
    }
  };

  return (
    <div className={isDarkMode ? 'dark' : ''}>
      <div className="app bg-primary text-white flex">
        {/* Left Panel */}
        <div className="left-panel box p-4">
          <div className="mb-4">
            <img src="logo192.png" className="w-24 h-24 mx-auto" alt="Logo" />
          </div>
          <div className="space-y-4">
            <label className="btn-box">
              <FaUpload size={24} />
              <input type="file" accept=".mp3,.wav" onChange={handleFileUpload} hidden />
              <span>Upload</span>
            </label>
            <button className="btn-box" onClick={handleMicClick}>
              <FaMicrophone size={24} />
              <span>{isRecording ? `${recordingSeconds}s` : 'Mic'}</span>
            </button>
            <button className="btn-box" onClick={handlePDFExport}>
              <FaFilePdf size={24} />
              <span>Export</span>
            </button>
            <button onClick={() => setIsDarkMode(!isDarkMode)} className="btn-box">
              {isDarkMode ? <FaSun size={24} /> : <FaMoon size={24} />}
              <span>Theme</span>
            </button>
          </div>
          <p className="text-xs mt-4 text-center">Upload or record call â†’ Ask questions â†’ Export</p>
        </div>

        {/* Chat Panel */}
        <div className="chat-panel box flex flex-col">
          <div className="chat-title">AI Call Summarization Chatbot</div>
          <div className="chat-window" ref={chatWindowRef}>
            {messages.map((msg, i) => (
              <div key={i} className={`chat-message ${msg.sender}`}>
                <span className="icon">{msg.sender === 'user' ? 'ðŸ§‘' : 'ðŸ¤–'}</span>
                <span>{msg.text}</span>
              </div>
            ))}
            {isTyping && <div className="chat-message bot typing">ðŸ¤– Typing...</div>}
          </div>
          <div className="chat-input">
            <input
              type="text"
              value={input}
              onChange={e => setInput(e.target.value)}
              onKeyDown={e => e.key === 'Enter' && handleSend()}
              placeholder="Type your question..."
            />
          </div>
        </div>

        {/* Right Panel */}
        <div className="right-panel box p-4 space-y-4">
          <div className="waveform-container" ref={waveformRef}></div>
          <div className="transcription-box">
            <h2 className="text-sm font-bold mb-1">Transcript Preview</h2>
            <textarea
              className="scrollable-box resize-y"
              value={transcription}
              readOnly
            />
          </div>
        </div>
      </div>
    </div>
  );
}

export default App;
