import React, { useState, useEffect, useRef } from 'react';
import MicRecorder from 'mic-recorder-to-mp3';
import { FaMicrophone, FaStop, FaRobot, FaUserCircle, FaFilePdf, FaUpload } from 'react-icons/fa';

const recorder = new MicRecorder({ bitRate: 128 });

function App() {
  const [messages, setMessages] = useState([]);
  const [input, setInput] = useState('');
  const [transcription, setTranscription] = useState('');
  const [isRecording, setIsRecording] = useState(false);
  const [recordingSeconds, setRecordingSeconds] = useState(30);
  const [audioUrl, setAudioUrl] = useState('');
  const intervalRef = useRef(null);
  const chatRef = useRef(null);

  useEffect(() => {
    if (chatRef.current) {
      chatRef.current.scrollTop = chatRef.current.scrollHeight;
    }
  }, [messages]);

  const handleSend = async () => {
    if (!input.trim() || !transcription) return;
    const userMessage = { text: input, sender: 'user' };
    setMessages(prev => [...prev, userMessage]);
    setInput('');

    const res = await fetch('http://localhost:8000/chat', {
      method: 'POST',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify({ prompt: input, context: transcription })
    });
    const data = await res.json();
    setMessages(prev => [...prev, { text: data.response, sender: 'bot' }]);
  };

  const handleFileUpload = async (e) => {
    const file = e.target.files[0];
    if (!file) return;
    const formData = new FormData();
    formData.append('audio', file);
    const res = await fetch('http://localhost:8000/transcribe', {
      method: 'POST',
      body: formData
    });
    const data = await res.json();
    if (!data.transcription || data.transcription.includes("No voice detected")) {
      alert("No voice detected in the audio.");
    }
    setTranscription(data.transcription || '');
  };

  const handlePDFExport = async () => {
    if (!transcription) return alert("Please upload or record audio first.");
    const res = await fetch('http://localhost:8000/export/pdf', {
      method: 'POST',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify({ prompt: transcription })
    });
    const data = await res.json();
    if (data.file) window.open('http://localhost:8000/' + data.file, '_blank');
    else alert("PDF generation failed.");
  };

  const stopRecording = async () => {
    const [buffer, blob] = await recorder.stop().getMp3();
    const file = new File([blob], "mic_recording.mp3", { type: "audio/mp3" });
    const url = URL.createObjectURL(blob);
    setAudioUrl(url);
    setIsRecording(false);
    setRecordingSeconds(30);
    clearInterval(intervalRef.current);
    const formData = new FormData();
    formData.append("audio", file);
    const res = await fetch("http://localhost:8000/transcribe", {
      method: "POST",
      body: formData
    });
    const data = await res.json();
    if (!data.transcription || data.transcription.includes("No voice detected")) {
      alert("No voice detected in the audio.");
    }
    setTranscription(data.transcription || '');
  };

  const handleMicClick = async () => {
    if (!isRecording) {
      try {
        await recorder.start();
        setIsRecording(true);
        intervalRef.current = setInterval(() => {
          setRecordingSeconds(prev => {
            if (prev <= 1) {
              stopRecording();
              return 0;
            }
            return prev - 1;
          });
        }, 1000);
      } catch (err) {
        console.error("Mic error:", err);
        alert("Microphone access denied.");
      }
    } else {
      stopRecording();
    }
  };

  return (
    <div className="flex h-screen bg-gradient-to-br from-gray-900 via-gray-800 to-black text-white">
      {/* Left Panel */}
      <div className="w-1/5 bg-gray-800 rounded-xl p-4 flex flex-col items-center justify-between shadow-xl">
        <img src="/logo.png" alt="Logo" className="h-16" />
        <img src="/call_center.jpg" alt="Call Center" className="h-48 rounded-lg mt-4" />
        <div className="flex flex-col space-y-4 mt-6">
          <label className="cursor-pointer flex items-center space-x-2">
            <FaUpload />
            <input type="file" accept=".mp3,.wav" onChange={handleFileUpload} hidden />
          </label>
          <button onClick={handleMicClick}>
            {isRecording ? <FaStop /> : <FaMicrophone />}
          </button>
          <button onClick={handlePDFExport}><FaFilePdf /></button>
        </div>
        {isRecording && <p className="text-yellow-400 mt-2">Recording... {recordingSeconds}s</p>}
      </div>

      {/* Center Panel */}
      <div className="w-3/5 bg-gray-900 rounded-xl p-4 mx-4 flex flex-col shadow-lg">
        <h2 className="text-2xl font-bold text-center mb-2">üß† AI Call Summarization Chatbot</h2>
        <div ref={chatRef} className="flex-1 overflow-y-auto p-2 space-y-3">
          {messages.map((msg, idx) => (
            <div key={idx} className={`flex items-center space-x-3 ${msg.sender === 'user' ? 'justify-end' : 'justify-start'}`}>
              <span>{msg.sender === 'user' ? 'üßë‚Äçüíº' : 'ü§ñ'}</span>
              <div className={`p-3 rounded-xl max-w-md ${msg.sender === 'user' ? 'bg-blue-600 text-white' : 'bg-gray-700'}`}>
                {msg.text}
              </div>
            </div>
          ))}
        </div>
        <div className="mt-3">
          <input
            type="text"
            value={input}
            onChange={e => setInput(e.target.value)}
            onKeyDown={e => e.key === 'Enter' && handleSend()}
            placeholder="Ask me about the call..."
            className="w-full p-3 rounded-lg bg-gray-800 text-white border border-gray-600"
          />
        </div>
      </div>

      {/* Right Panel */}
      <div className="w-1/5 bg-gray-800 rounded-xl p-4 flex flex-col shadow-xl">
        <div className="mb-3 text-sm text-gray-400">üìÉ Transcript Preview</div>
        <textarea
          className="flex-1 rounded-lg p-3 bg-black text-white border border-gray-600 resize-y overflow-auto"
          value={transcription}
          readOnly
        />
        {audioUrl && (
          <audio className="mt-3" controls src={audioUrl} />
        )}
      </div>
    </div>
  );
}

export default App;
