// src/App.jsx

import React, { useState } from "react";
import { uploadAudioFile, startMicRecording } from "./audioUtils";
import "./App.css";

function App() {
  const [transcript, setTranscript] = useState("");
  const [theme, setTheme] = useState("light");
  const [recording, setRecording] = useState(null);
  const [chat, setChat] = useState([]);
  const [audioBlob, setAudioBlob] = useState(null);

  const toggleTheme = () =>
    setTheme((prev) => (prev === "light" ? "dark" : "light"));

  const handleUpload = async (e) => {
    const file = e.target.files[0];
    if (!file) return;
    const text = await uploadAudioFile(file);
    setTranscript(text);
    addToChat("Audio Transcript", text);
  };

  const handleMic = async () => {
    if (recording) {
      recording.recorder.stop();
      recording.stream.getTracks().forEach((track) => track.stop());
      setRecording(null);

      const text = await uploadAudioFile(audioBlob);
      setTranscript(text);
      addToChat("Mic Transcript", text);
    } else {
      startMicRecording(setRecording, setAudioBlob);
    }
  };

  const addToChat = (label, text) => {
    const timestamp = new Date().toLocaleTimeString();
    setChat((prev) => [...prev, { label, text, timestamp }]);
  };

  const exportAs = (type) => {
    const blob = new Blob(
      [chat.map((msg) => `[${msg.timestamp}] ${msg.label}: ${msg.text}`).join("\n\n")],
      { type: type === "pdf" ? "application/pdf" : "application/msword" }
    );
    const link = document.createElement("a");
    link.href = URL.createObjectURL(blob);
    link.download = `chat_summary.${type}`;
    link.click();
  };

  return (
    <div className={`app ${theme}`}>
      <header>
        <h1>AI Call Summarizer</h1>
        <button onClick={toggleTheme}>
          Toggle {theme === "light" ? "Dark" : "Light"}
        </button>
      </header>

      <main className="main-layout">
        <aside className="left-panel">
          <label className="upload-label">
            Upload Audio
            <input type="file" accept="audio/*" onChange={handleUpload} hidden />
          </label>
          <button onClick={handleMic}>
            {recording ? "Stop Recording" : "Mic Record"}
          </button>
          <div className="export-buttons">
            <button onClick={() => exportAs("pdf")}>Export PDF</button>
            <button onClick={() => exportAs("doc")}>Export Word</button>
          </div>
        </aside>

        <section className="chat-panel">
          {chat.map((msg, idx) => (
            <div key={idx} className="chat-msg">
              <span className="timestamp">[{msg.timestamp}]</span>
              <strong>{msg.label}:</strong>
              <p>{msg.text}</p>
            </div>
          ))}
        </section>

        <aside className="right-panel">
          <h2>Transcript Preview</h2>
          <div className="transcript-box">{transcript}</div>
        </aside>
      </main>
    </div>
  );
}

export default App;
