import React, { useState, useEffect, useRef } from "react";
import "./App.css";
import { startMicRecording, stopMicRecording } from "./audioUtils";

function App() {
  const [isRecording, setIsRecording] = useState(false);
  const [chatMessages, setChatMessages] = useState([]);
  const [theme, setTheme] = useState("light");

  const handleStartRecording = async () => {
    setIsRecording(true);
    await startMicRecording(handleAudioStop);
  };

  const handleStopRecording = () => {
    stopMicRecording();
    setIsRecording(false);
  };

  const handleAudioStop = async (audioBlob) => {
    const formData = new FormData();
    formData.append("file", audioBlob, "recorded_audio.webm");

    const response = await fetch("http://127.0.0.1:8000/transcribe", {
      method: "POST",
      body: formData,
    });

    if (response.ok) {
      const data = await response.json();
      const userMessage = { sender: "user", text: data.transcription, time: new Date().toLocaleTimeString() };
      setChatMessages(prev => [...prev, userMessage]);
      queryLLM(data.transcription);
    }
  };

  const queryLLM = async (prompt) => {
    const response = await fetch("http://127.0.0.1:8000/chat", {
      method: "POST",
      headers: { "Content-Type": "application/json" },
      body: JSON.stringify({ prompt }),
    });

    if (response.ok) {
      const data = await response.json();
      const botMessage = { sender: "bot", text: data.response, time: new Date().toLocaleTimeString() };
      setChatMessages(prev => [...prev, botMessage]);
    }
  };

  const toggleTheme = () => {
    setTheme(prev => (prev === "light" ? "dark" : "light"));
  };

  return (
    <div className={`app ${theme}`}>
      <header>
        <h1>üéôÔ∏è AI Call Summarizer</h1>
        <button onClick={toggleTheme}>Toggle Theme</button>
      </header>

      <main>
        <div className="controls">
          {!isRecording ? (
            <button onClick={handleStartRecording}>üé§ Start Mic</button>
          ) : (
            <button onClick={handleStopRecording}>‚èπÔ∏è Stop Mic</button>
          )}
        </div>

        <div className="chat-box">
          {chatMessages.map((msg, index) => (
            <div key={index} className={`chat-msg ${msg.sender}`}>
              <div><b>{msg.sender === "user" ? "You" : "Bot"}</b> [{msg.time}]:</div>
              <div>{msg.text}</div>
            </div>
          ))}
        </div>
      </main>
    </div>
  );
}

export default App;
