import React, { useState, useRef, useEffect } from 'react';
import MicRecorder from 'mic-recorder-to-mp3';
import { FaUpload, FaMicrophone, FaFilePdf, FaUserCircle, FaRobot } from 'react-icons/fa';
import './App.css';

const recorder = new MicRecorder({ bitRate: 128 });

export default function App() {
  const [messages, setMessages] = useState([]);
  const [input, setInput] = useState('');
  const [transcription, setTranscription] = useState('');
  const [isRecording, setIsRecording] = useState(false);
  const [audioUrl, setAudioUrl] = useState('');
  const chatRef = useRef(null);

  useEffect(() => {
    if (chatRef.current) chatRef.current.scrollTop = chatRef.current.scrollHeight;
  }, [messages]);

  const handleSend = async () => {
    if (!input.trim() || !transcription) return;
    const userMessage = { text: input, sender: 'user' };
    setMessages(prev => [...prev, userMessage]);
    setInput('');
    const res = await fetch('http://localhost:8000/chat', {
      method: 'POST',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify({ prompt: input, context: transcription })
    });
    const data = await res.json();
    setMessages(prev => [...prev, { text: data.response, sender: 'bot' }]);
  };

  const handleFileUpload = async (e) => {
    const file = e.target.files[0];
    if (!file) return;
    const formData = new FormData();
    formData.append('audio', file);
    const res = await fetch('http://localhost:8000/transcribe', {
      method: 'POST',
      body: formData
    });
    const data = await res.json();
    setTranscription(data.transcription || '');
  };

  const handlePDFExport = async () => {
    if (!transcription) return alert("No transcript found");
    const res = await fetch('http://localhost:8000/export/pdf', {
      method: 'POST',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify({ prompt: transcription })
    });
    const data = await res.json();
    if (data.file) window.open('http://localhost:8000' + data.file, '_blank');
  };

  const stopRecording = async () => {
    const [buffer, blob] = await recorder.stop().getMp3();
    const file = new File([blob], "recording.mp3", { type: "audio/mp3" });
    const formData = new FormData();
    formData.append("audio", file);
    setIsRecording(false);
    const res = await fetch("http://localhost:8000/transcribe", {
      method: "POST",
      body: formData
    });
    const data = await res.json();
    setTranscription(data.transcription || '');
  };

  const handleMicClick = async () => {
    if (!isRecording) {
      await recorder.start();
      setIsRecording(true);
    } else {
      await stopRecording();
    }
  };

  return (
    <div className="flex h-screen bg-gradient-to-br from-gray-900 to-gray-800 text-white">
      {/* Left Panel */}
      <div className="w-1/5 flex flex-col justify-between items-center bg-black/20 p-4 rounded-lg border border-white/10">
        <img src="/logo.png" alt="Logo" className="w-24 h-24 mb-4" />
        <div className="flex flex-col gap-4 w-full items-center">
          <label className="w-full">
            <div className="bg-blue-600 hover:bg-blue-700 py-2 w-full text-center rounded-lg text-white cursor-pointer flex items-center justify-center gap-2">
              <FaUpload /> Upload
            </div>
            <input type="file" accept=".mp3,.wav" className="hidden" onChange={handleFileUpload} />
          </label>
          <button onClick={handleMicClick} className="bg-red-600 hover:bg-red-700 py-2 w-full rounded-lg flex items-center justify-center gap-2">
            <FaMicrophone /> {isRecording ? "Stop" : "Record"}
          </button>
          <button onClick={handlePDFExport} className="bg-green-600 hover:bg-green-700 py-2 w-full rounded-lg flex items-center justify-center gap-2">
            <FaFilePdf /> Export PDF
          </button>
        </div>
        <img src="/call_center.jpg" alt="Call Center" className="w-full mt-4 rounded-lg" />
      </div>

      {/* Center Chat */}
      <div className="w-3/5 px-4 flex flex-col justify-between">
        <h1 className="text-center text-lg font-semibold py-4">AI Call Summarization Chatbot</h1>
        <div className="flex-1 overflow-y-auto px-4" ref={chatRef}>
          {messages.map((msg, i) => (
            <div key={i} className={`my-2 p-3 rounded-lg max-w-full ${msg.sender === 'user' ? 'bg-yellow-500 text-black self-end' : 'bg-cyan-700 text-white self-start'}`}>
              <div className="flex items-center gap-2">
                {msg.sender === 'user' ? <FaUserCircle /> : <FaRobot />}
                <span>{msg.text}</span>
              </div>
            </div>
          ))}
        </div>
        <div className="flex mt-4">
          <input
            className="flex-1 px-4 py-2 rounded-l-lg bg-gray-900 text-white border border-gray-700 focus:outline-none"
            value={input}
            onChange={(e) => setInput(e.target.value)}
            placeholder="Type your query..."
            onKeyDown={e => e.key === 'Enter' && handleSend()}
          />
          <button onClick={handleSend} className="px-4 bg-blue-500 hover:bg-blue-600 rounded-r-lg">Send</button>
        </div>
      </div>

      {/* Right Panel */}
      <div className="w-1/5 p-4 bg-black/20 rounded-lg border border-white/10 flex flex-col">
        <div className="mb-4">
          <strong className="block mb-1">🎵 Audio Visualization</strong>
          <div className="bg-black h-24 rounded-md flex items-center justify-center text-gray-400 border border-dashed border-gray-600">
            Waveform preview
          </div>
        </div>
        <div className="flex flex-col flex-grow">
          <strong className="mb-1">📝 Transcript Preview</strong>
          <textarea
            value={transcription}
            readOnly
            className="flex-1 resize-y bg-black text-white p-2 rounded-md border border-gray-700 overflow-y-auto"
            style={{ minHeight: "150px" }}
          />
        </div>
      </div>
    </div>
  );
}
