// App.jsx
import React, { useState } from "react";
import "./App.css";
import { uploadAudioFile, startMicRecording } from "./audioUtils";

function App() {
  const [theme, setTheme] = useState("dark");
  const [transcript, setTranscript] = useState("");
  const [chat, setChat] = useState([]);
  const [loading, setLoading] = useState(false);

  const toggleTheme = () => {
    setTheme((prev) => (prev === "dark" ? "light" : "dark"));
  };

  const handleAudioUpload = async (e) => {
    const file = e.target.files[0];
    if (!file) return;
    setLoading(true);
    const result = await uploadAudioFile(file);
    if (result.error) {
      alert(result.error);
    } else {
      setTranscript(result.transcript);
      addToChat("You uploaded audio. AI is analyzing...");
      addToChat(result.summary);
    }
    setLoading(false);
  };

  const handleMic = async () => {
    setLoading(true);
    const result = await startMicRecording();
    if (result.error) {
      alert(result.error);
    } else {
      setTranscript(result.transcript);
      addToChat("Mic recording complete. AI is analyzing...");
      addToChat(result.summary);
    }
    setLoading(false);
  };

  const addToChat = (message) => {
    const timestamp = new Date().toLocaleTimeString();
    setChat((prev) => [...prev, { message, timestamp }]);
  };

  const exportPDF = () => {
    window.open("http://localhost:8000/export/pdf", "_blank");
  };

  const exportWord = () => {
    window.open("http://localhost:8000/export/word", "_blank");
  };

  return (
    <div className={`app-container ${theme}`}>
      <div className="header">
        <div className="title">🎧 AI Call Summarizer</div>
        <div className="controls">
          <label htmlFor="audio-upload" className="btn">📤 Upload</label>
          <input id="audio-upload" type="file" accept="audio/*" onChange={handleAudioUpload} hidden />
          <button onClick={handleMic} className="btn">🎙️ Mic</button>
          <button onClick={toggleTheme} className="btn">🌓 {theme === "dark" ? "Dark" : "Light"}</button>
          <button onClick={exportPDF} className="btn">📄 Export PDF</button>
          <button onClick={exportWord} className="btn">📄 Export Word</button>
        </div>
      </div>

      <div className="main">
        <div className="chat-box">
          {chat.map((item, index) => (
            <div key={index} className="chat-msg">
              <span className="timestamp">[{item.timestamp}]</span> {item.message}
            </div>
          ))}
          {loading && <div className="chat-msg loading">Processing...</div>}
        </div>
        <div className="transcript-panel">
          <h3>📝 Transcript Preview</h3>
          <div className="transcript-text">{transcript}</div>
        </div>
      </div>
    </div>
  );
}

export default App;
