import React, { useState, useEffect, useRef } from 'react';
import MicRecorder from 'mic-recorder-to-mp3';
import { FaMicrophone, FaFilePdf, FaUpload } from 'react-icons/fa';

const recorder = new MicRecorder({ bitRate: 128 });

function App() {
  const [messages, setMessages] = useState([]);
  const [input, setInput] = useState('');
  const [transcription, setTranscription] = useState('');
  const [isRecording, setIsRecording] = useState(false);
  const [audioUrl, setAudioUrl] = useState('');
  const chatRef = useRef(null);

  useEffect(() => {
    chatRef.current?.scrollIntoView({ behavior: 'smooth' });
  }, [messages]);

  const handleSend = async () => {
    if (!input.trim() || !transcription) return;
    setMessages(prev => [...prev, { sender: 'user', text: input }]);
    setInput('');
    const res = await fetch('http://localhost:8000/chat', {
      method: 'POST',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify({ prompt: input, context: transcription })
    });
    const data = await res.json();
    setMessages(prev => [...prev, { sender: 'bot', text: data.response }]);
  };

  const handleUpload = async (e) => {
    const file = e.target.files[0];
    if (!file) return;
    const form = new FormData();
    form.append("audio", file);
    const res = await fetch('http://localhost:8000/transcribe', {
      method: 'POST',
      body: form
    });
    const data = await res.json();
    setTranscription(data.transcription || '');
  };

  const handleRecord = async () => {
    if (!isRecording) {
      await recorder.start();
      setIsRecording(true);
    } else {
      const [buffer, blob] = await recorder.stop().getMp3();
      const file = new File([blob], "mic.mp3", { type: "audio/mp3" });
      const url = URL.createObjectURL(blob);
      setAudioUrl(url);
      const form = new FormData();
      form.append("audio", file);
      const res = await fetch("http://localhost:8000/transcribe", {
        method: "POST",
        body: form
      });
      const data = await res.json();
      setTranscription(data.transcription || '');
      setIsRecording(false);
    }
  };

  const handleExportPDF = async () => {
    const res = await fetch("http://localhost:8000/export/pdf", {
      method: "POST",
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify({ prompt: transcription })
    });
    const data = await res.json();
    if (data.file) window.open("http://localhost:8000" + data.file);
  };

  return (
    <div className="flex h-screen bg-primary text-white p-4 gap-4">
      {/* LEFT PANEL */}
      <div className="w-1/5 bg-panel rounded-box p-4 flex flex-col gap-4 shadow-lg">
        <h2 className="text-xl font-bold text-accent text-center">Upload / Export</h2>
        <label className="bg-gray-800 rounded-lg p-4 text-center cursor-pointer hover:bg-gray-700">
          <FaUpload className="mx-auto text-3xl mb-2" />
          Upload Audio
          <input type="file" accept=".mp3,.wav" onChange={handleUpload} hidden />
        </label>
        <button onClick={handleRecord} className="bg-gray-800 rounded-lg p-4 hover:bg-gray-700">
          <FaMicrophone className="mx-auto text-3xl mb-2" />
          {isRecording ? "Stop Recording" : "Start Mic"}
        </button>
        <button onClick={handleExportPDF} className="bg-gray-800 rounded-lg p-4 hover:bg-gray-700">
          <FaFilePdf className="mx-auto text-3xl mb-2" />
          Export PDF
        </button>
      </div>

      {/* CENTER PANEL */}
      <div className="w-3/5 bg-panel rounded-box p-4 flex flex-col shadow-lg">
        <h2 className="text-2xl font-bold mb-2 text-center text-accent">ðŸ’¬ AI Call Summarization Chatbot</h2>
        <div className="flex-1 overflow-y-auto space-y-4 px-2">
          {messages.map((msg, i) => (
            <div key={i} className={`flex ${msg.sender === 'user' ? 'justify-end' : 'justify-start'}`}>
              <div className={`max-w-sm p-3 rounded-lg ${msg.sender === 'user' ? 'bg-chatUser text-black' : 'bg-chatBot text-black'}`}>
                {msg.sender === 'user' ? 'ðŸ§‘ ' : 'ðŸ¤– '}
                {msg.text}
              </div>
            </div>
          ))}
          <div ref={chatRef} />
        </div>
        <div className="mt-4 flex">
          <input
            className="flex-1 p-2 rounded-lg bg-gray-800 text-white"
            placeholder="Type your question..."
            value={input}
            onChange={e => setInput(e.target.value)}
            onKeyDown={e => e.key === 'Enter' && handleSend()}
          />
        </div>
      </div>

      {/* RIGHT PANEL */}
      <div className="w-1/5 bg-panel rounded-box p-4 flex flex-col shadow-lg">
        <h2 className="text-xl font-bold mb-2 text-center text-accent">Transcript</h2>
        <div className="h-40 bg-black/40 rounded-lg text-sm p-2 overflow-y-auto">
          {transcription}
        </div>
        {isRecording && (
          <div className="mt-4 text-yellow-300 text-sm font-bold text-center">
            ðŸŽ™ Recording... Please wait
            <div className="waveform-placeholder mt-2 h-10 w-full border border-dashed border-yellow-400 rounded-lg flex items-center justify-center text-yellow-400">
              ðŸ”Š Voice waveform...
            </div>
          </div>
        )}
        {audioUrl && <audio className="mt-4" controls src={audioUrl} />}
      </div>
    </div>
  );
}

export default App;
